mapper.select:
SELECT * FROM (
SELECT rownum rnum, m.* FROM (
SELECT user_id, email, input_date
FROM member


		AND user_id LIKE '%' || #{keyword} || '%'


ORDER BY input_date DESC
) m
) WHERE rnum BETWEEN #{startNum} AND #{endNum}

SELECT * FROM ( SELECT rownum rnum, a.* FROM ( SELECT p.product_num, p.title, p.price, p.status_code, TO_CHAR(p.input_date, 'YYYY-MM-DD') as p_input_date, sp.addr, (SELECT COUNT(*) FROM bookmark b WHERE b.product_num = p.product_num) as bookmark_count FROM product p LEFT JOIN sell_place sp ON p.product_num = sp.product_num WHERE p.store_num = (SELECT store_num FROM store WHERE user_id = #{userId}) AND p.delete_flag = 'N'
ORDER BY p.view_count DESC ORDER BY p.price ASC ORDER BY p.price DESC ORDER BY p.input_date DESC
) a ) WHERE rnum BETWEEN #{start} AND #{end}
