mapper.select:
SELECT * FROM (SELECT rownum rnum, p.* FROM ( SELECT product_num, title, price, addr, input_date, product_status, thumbnail FROM product
AND title LIKE '%' || #{keyword} || '%'
ORDER BY
price ASC price DESC input_date DESC
) p ) WHERE rnum BETWEEN #{startNum} AND #{endNum}

mapper.update:
UPDATE  product 
SET     product_status = #{sellState} 
WHERE   product_num = #{productNo}
