mapper.select:
SELECT COUNT(*) FROM product

	 AND category_code = #{category_code} 
	 AND title LIKE '%' || #{keyword} || '%'

SELECT * FROM ( SELECT rownum rnum, p_sub.* FROM ( SELECT p.product_num, p.title, p.price, s_p.addr, p.input_date, p.modify_date, ss.status_text AS product_status, p.status_code, p.thumbnail, p.delete_flag /* [추가] 목록에서 '삭제된 상품' 레이아웃 처리를 위해 필요 */ FROM product p LEFT OUTER JOIN sell_place s_p ON p.product_num = s_p.product_num LEFT OUTER JOIN sell_status ss ON p.status_code = ss.status_code
AND p.category_code = #{category_code} AND p.title LIKE '%' || #{keyword} || '%'
ORDER BY
p.price ASC p.price DESC p.input_date DESC
) p_sub ) WHERE rnum BETWEEN #{startNum} AND #{endNum}

SELECT product_img
FROM product_img
WHERE product_num = #{product_num}
