mapper.select:
select PRODUCTNUM, PRODUCTNAME, PRICE, THUMBNAIL, TIMDIF, SELLSTATECODE, TRADINGAREA, SELLSTATE, LASTMODIFY, bookmarkFlag from (select pv.productNum productNum, pv.productName productName, pv.PRICE price, pv.THUMBNAIL thumbnail, pv.timDif timDif, pv.sellStateCode sellStateCode, pv.tradingArea tradingArea, pv.sellState sellState, pv.lastModify lastModify, pv.timeString timeString, pv.VIEW_COUNT VIEW_COUNT, pv.category category, CASE WHEN b.PRODUCT_NUM IS NOT NULL THEN 'Y' ELSE 'N' END AS bookmarkFlag, rownum r from product_view pv LEFT OUTER JOIN bookmark b ON pv.productNum = b.product_num
AND b.store_num = #{userId}
AND b.store_num = 0
and instr( productName, #{keyword} ) != 0
and category = #{category}
and price between #{startPrice} and #{endPrice}
order by pv.PRICE ASC order by pv.PRICE DESC order by pv.lastModify DESC
)
r between #{startNum} and #{endNum}
